package com.huaxun.view;import android.content.Context;import android.support.v4.view.ViewPager;import android.util.AttributeSet;import android.view.MotionEvent;public class NewsDetailViewPager extends ViewPager {		public NewsDetailViewPager(Context context) {		super(context);	}	public NewsDetailViewPager(Context context, AttributeSet attrs) {		super(context, attrs);	}	 @Override	 public boolean dispatchTouchEvent(MotionEvent ev) {		getParent().requestDisallowInterceptTouchEvent(true);	    return super.dispatchTouchEvent(ev);	 }	 }